import { Typography, TextField, Button, CircularProgress } from '@mui/material';
import { useState, useEffect } from 'react';
import { fetchWeather, fetchForecast } from './weatherApi';
import WeatherCard from './WeatherCard';
import AdBanner from './AdBanner';
import { motion } from "framer-motion";
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  typography: {
    fontFamily: [
      'Montserrat',
      'Arial',
      'sans-serif',
    ].join(','),
  },
});

// --- ЭФФЕКТЫ ---
function CloudsEffect() {
  return (
    <>
      {[...Array(4)].map((_, i) => (
        <motion.div
          key={i}
          style={{
            position: "absolute",
            left: `${i * 24 + 7}%`,
            top: `${40 + i * 18}px`,
            width: 110 + i * 32,
            height: 42 + i * 19,
            background: "rgba(255,255,255,0.60)",
            borderRadius: "50%",
            filter: "blur(14px)",
            opacity: 0.58 - i * 0.1,
            zIndex: 0,
            pointerEvents: "none"
          }}
          animate={{
            x: [0, 28 + i * 11, 0]
          }}
          transition={{
            duration: 29 + i * 6,
            repeat: Infinity,
            repeatType: "mirror"
          }}
        />
      ))}
    </>
  );
}
function RainEffect() {
  return (
    <>
      {[...Array(18)].map((_, i) => {
        const isGlow = i % 3 === 0;
        return (
          <motion.div
            key={i}
            style={{
              position: "absolute",
              left: `${6 + Math.random() * 88}%`,
              top: `${-24 + Math.random() * 18}%`,
              width: 8 + Math.random() * 9,
              height: 28 + Math.random() * 20,
              background: isGlow
                ? "linear-gradient(180deg,#60a5fa88 0%,#38bdf8bb 100%)"
                : "rgba(92,160,240,0.20)",
              borderRadius: "40%",
              filter: isGlow ? "blur(3px)" : "blur(1.1px)",
              boxShadow: isGlow ? "0 0 16px 3px #38bdf8aa" : undefined,
              opacity: isGlow ? 0.5 : 0.32,
              zIndex: 1,
              pointerEvents: "none"
            }}
            animate={{
              y: ["0%", "128%"],
              opacity: [0.45, 0.63, 0]
            }}
            transition={{
              duration: 2.8 + Math.random() * 2.7,
              repeat: Infinity,
              delay: i * 0.23
            }}
          />
        );
      })}
    </>
  );
}
function SnowEffect() {
  return (
    <>
      {[...Array(20)].map((_, i) => {
        const isLight = i % 4 === 0;
        return (
          <motion.div
            key={i}
            style={{
              position: "absolute",
              left: `${2 + Math.random() * 95}%`,
              top: `${-14 + Math.random() * 14}%`,
              width: isLight ? 9 : 13 + Math.random() * 8,
              height: isLight ? 9 : 13 + Math.random() * 8,
              background: isLight
                ? "rgba(230,240,255,0.09)"
                : "rgba(210,230,255,0.22)",
              borderRadius: "50%",
              filter: isLight ? "blur(2.3px)" : "blur(1.6px)",
              opacity: isLight ? 0.16 : 0.27,
              zIndex: 1,
              pointerEvents: "none"
            }}
            animate={{
              y: ["0%", "160%"],
              x: [0, 12 - Math.random() * 18, 0],
              opacity: isLight
                ? [0.12, 0.15, 0.10]
                : [0.20, 0.34, 0.13]
            }}
            transition={{
              duration: 7 + Math.random() * 3.9,
              repeat: Infinity,
              delay: i * 0.56
            }}
          />
        );
      })}
    </>
  );
}
function SunEffect() {
  return (
    <>
      {/* Большой glow */}
      <motion.div
        style={{
          position: "absolute",
          right: 60,
          top: 32,
          width: 160,
          height: 160,
          borderRadius: "50%",
          background: "radial-gradient(circle at 50% 45%, #fffcd988 55%, #fbbf2499 100%)",
          boxShadow: "0 0 84px 30px #fffec966",
          zIndex: 2,
          opacity: 0.79,
          pointerEvents: "none"
        }}
        animate={{
          scale: [1, 1.06, 0.98, 1],
          opacity: [0.79, 0.93, 0.68, 0.79],
          filter: [
            "blur(2.3px)",
            "blur(4.3px)",
            "blur(2.3px)",
            "blur(1.4px)"
          ]
        }}
        transition={{
          duration: 10,
          repeat: Infinity,
          repeatType: "mirror"
        }}
      />
      {/* Малое солнце */}
      <motion.div
        style={{
          position: "absolute",
          right: 112,
          top: 72,
          width: 60,
          height: 60,
          borderRadius: "50%",
          background: "radial-gradient(circle, #ffe37d 70%, #fff0 100%)",
          boxShadow: "0 0 22px 6px #ffef6e88",
          zIndex: 3,
          opacity: 0.91,
          pointerEvents: "none"
        }}
        animate={{
          scale: [1, 1.09, 1.04, 1],
          opacity: [0.91, 0.99, 0.81, 0.91]
        }}
        transition={{
          duration: 5,
          repeat: Infinity,
          repeatType: "mirror"
        }}
      />
    </>
  );
}
function NightGlow() {
  return (
    <>
      <motion.div
        style={{
          position: "absolute",
          left: "9%",
          top: "-16%",
          width: "82%",
          height: 230,
          borderRadius: "50%",
          background: "linear-gradient(135deg, #29297a66 0%, #25284dbb 100%)",
          filter: "blur(40px)",
          opacity: 0.82,
          zIndex: 1,
          pointerEvents: "none"
        }}
        animate={{ scale: [1, 1.12, 0.93, 1] }}
        transition={{ duration: 17, repeat: Infinity, repeatType: "mirror" }}
      />
      <motion.div
        style={{
          position: "absolute",
          left: "62%",
          bottom: "-11%",
          width: 180,
          height: 120,
          borderRadius: "50%",
          background: "linear-gradient(135deg, #7d86c799 0%, #1a203bff 100%)",
          filter: "blur(44px)",
          opacity: 0.47,
          zIndex: 1,
          pointerEvents: "none"
        }}
        animate={{ scale: [1, 1.07, 0.98, 1] }}
        transition={{ duration: 23, repeat: Infinity, repeatType: "mirror" }}
      />
    </>
  );
}

// --- UNSPLASH ФУНКЦИЯ ---
async function getCityPhoto(city) {
  const ACCESS_KEY = "ТВОЙ_UNSPLASH_ACCESS_KEY"; // <--- свой ключ!
  const url = `https://api.unsplash.com/photos/random?query=${encodeURIComponent(city)}%20landmark&orientation=landscape&client_id=${ACCESS_KEY}`;
  try {
    const res = await fetch(url);
    const data = await res.json();
    return data?.urls?.regular || null;
  } catch {
    return null;
  }
}

function getBackground(desc, isNight) {
  if (isNight) return "linear-gradient(135deg, #232942 0%, #465077 100%)";
  if (!desc) return "linear-gradient(135deg, #c7e0f5 0%, #b4c7e7 40%, #b3c2d7 100%)";
  desc = desc.toLowerCase();
  if (desc.includes('дожд')) return "linear-gradient(135deg, #b3c2d7 0%, #90a4c3 100%)";
  if (desc.includes('гроза')) return "linear-gradient(135deg, #aeb7c6 0%, #8394b0 100%)";
  if (desc.includes('ясно')) return "linear-gradient(135deg, #ffe789 0%, #bde1ff 100%)";
  if (desc.includes('облачно')) return "linear-gradient(135deg, #c7e0f5 0%, #a0aec0 100%)";
  if (desc.includes('снег')) return "linear-gradient(135deg, #e0e8f0 0%, #b3c2d7 100%)";
  return "linear-gradient(135deg, #c7e0f5 0%, #b4c7e7 40%, #b3c2d7 100%)";
}

function formatTime(ts) {
  const date = new Date(ts * 1000);
  return date.toLocaleTimeString('ru-RU', { hour: '2-digit', minute: '2-digit' });
}

function playWeatherSound(desc) {
  let audio;
  if (desc.includes('дожд')) audio = new Audio('/rain.wav');
  else if (desc.includes('гроз')) audio = new Audio('/thunder.wav');
  else if (desc.includes('ясно')) audio = new Audio('/sun.wav');
  else return;
  audio.volume = 0.2;
  audio.play();
}

function isNightNow() {
  const h = new Date().getHours();
  return h < 6 || h >= 21;
}

function App() {
  const [city, setCity] = useState('Москва');
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState([]);
  const [photoUrl, setPhotoUrl] = useState(null);
  const [loading, setLoading] = useState(false);

  const isNight = isNightNow();
  const desc = weather?.desc || "";

  // Загрузка фото при смене города/погоды
  useEffect(() => {
    if (weather && weather.city) {
      getCityPhoto(weather.city).then(setPhotoUrl);
    }
  }, [weather && weather.city]);

  const handleShowWeather = async (name = city) => {
    setLoading(true);
    try {
      const data = await fetchWeather(name);
      const details = {
        feels: Math.round(data.main.feels_like),
        pressure: Math.round(data.main.pressure * 0.750062),
        humidity: data.main.humidity,
        wind: `${Math.round(data.wind.speed)} м/с`,
        sunrise: formatTime(data.sys.sunrise),
        sunset: formatTime(data.sys.sunset)
      };

      const forecastData = await fetchForecast(name);
      const now = Date.now();
      const hourly = [3, 6, 9].map(offset => {
        const dt = now + offset * 60 * 60 * 1000;
        const slot = forecastData.list.find(f =>
          Math.abs(new Date(f.dt * 1000).getHours() - new Date(dt).getHours()) < 2
        );
        return slot
          ? {
              time: new Date(slot.dt * 1000).toLocaleTimeString('ru-RU', { hour: "2-digit", minute: "2-digit" }),
              temp: Math.round(slot.main.temp),
              icon: `https://openweathermap.org/img/wn/${slot.weather[0].icon}@2x.png`
            }
          : null;
      }).filter(Boolean);

      setForecast(hourly);

      setWeather({
        city: data.name,
        temp: Math.round(data.main.temp),
        desc: data.weather[0].description[0].toUpperCase() + data.weather[0].description.slice(1),
        icon: `https://openweathermap.org/img/wn/${data.weather[0].icon}@4x.png`,
        details
      });
      playWeatherSound(data.weather[0].description);
    } catch {
      setWeather({ city: name, temp: '--', desc: 'Ошибка: город не найден', icon: '', details: {} });
      setForecast([]);
      setPhotoUrl(null);
    } finally {
      setLoading(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <motion.div
        key={desc + (isNight ? 'night' : 'day')}
        style={{
          minHeight: '100vh',
          paddingTop: "max(36px, env(safe-area-inset-top))",
          paddingBottom: 80,
          position: "relative",
          overflow: "hidden",
          fontFamily: 'Montserrat, Arial, sans-serif'
        }}
        animate={{ background: getBackground(desc, isNight) }}
        transition={{ duration: 1.2 }}
      >
        {/* --- UNSPLASH BACKGROUND НА ВЕСЬ ФОН --- */}
        {photoUrl && (
          <div
            style={{
              position: "absolute",
              zIndex: -2,
              top: 0, left: 0, width: "100vw", height: "100vh",
              backgroundImage: `url(${photoUrl})`,
              backgroundSize: "cover",
              backgroundPosition: "center",
              opacity: 0.14,
              filter: "blur(2.2px)",
              pointerEvents: "none"
            }}
          />
        )}

        {/* --- ЭФФЕКТЫ НА ВЕСЬ ФОН --- */}
        {desc && desc.toLowerCase().match(/облач|пасмур|cloud/) && <CloudsEffect />}
        {desc && desc.toLowerCase().includes('дожд') && <RainEffect />}
        {desc && desc.toLowerCase().includes('снег') && <SnowEffect />}
        {desc && desc.toLowerCase().includes('ясно') && !isNight && <SunEffect />}
        {isNight && <NightGlow />}

        {/* --- ДАЛЬШЕ ВЕСЬ ОСТАЛЬНОЙ ИНТЕРФЕЙС --- */}

        <motion.img
          src="/logo.png"
          alt="EasyWeather"
          style={{
            display: "block",
            margin: "32px auto 16px",
            width: 200,
            maxWidth: "90vw",
            height: "auto"
          }}
          animate={{
            scale: [1, 1.08, 0.97, 1],
            rotate: [0, 4, -2, 0],
            y: [0, -7, 0, 3, 0]
          }}
          transition={{
            duration: 3.8,
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />

        {/* --- ФОРМА, КНОПКИ, WeatherCard --- */}
        {/* ... ВСТАВЬ СЮДА СВОЙ UI ... */}
        {/* Пример — WeatherCard */}
        {weather && (
          <div style={{ marginTop: "-15px" }}>
            <WeatherCard {...weather} isNight={isNight} forecast={forecast} photoUrl={photoUrl} />
          </div>
        )}

        <AdBanner />
      </motion.div>
    </ThemeProvider>
  );
}

export default App;
