// weatherApi.js

export async function fetchWeather(city) {
  const apiKey = "61e36d8b6d0a171b685c5658b023c23a"; // вставь сюда свой ключ
  const url = `https://api.openweathermap.org/data/2.5/weather?q=${encodeURIComponent(city)}&appid=${apiKey}&lang=ru&units=metric`;
  const res = await fetch(url);
  if (!res.ok) throw new Error('Город не найден');
  return await res.json();
}

export async function fetchForecast(city) {
  const apiKey = "61e36d8b6d0a171b685c5658b023c23a"; // тот же ключ
  const url = `https://api.openweathermap.org/data/2.5/forecast?q=${encodeURIComponent(city)}&appid=${apiKey}&lang=ru&units=metric`;
  const res = await fetch(url);
  if (!res.ok) throw new Error('Не удалось получить прогноз');
  return await res.json();
}
