// Автоматическое определение ночного режима
function isNightNow() {
  const h = new Date().getHours();
  return h < 6 || h >= 21;
}

function App() {
  const [city, setCity] = useState('Москва');
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);

  const isNight = isNightNow();

  const handleShowWeather = async (name = city) => {
    setLoading(true);
    try {
      const data = await fetchWeather(name);
      const details = {
        feels: Math.round(data.main.feels_like),
        pressure: Math.round(data.main.pressure * 0.750062),
        humidity: data.main.humidity,
        wind: `${Math.round(data.wind.speed)} м/с`,
        sunrise: formatTime(data.sys.sunrise),
        sunset: formatTime(data.sys.sunset)
      };
      setWeather({
        city: data.name,
        temp: Math.round(data.main.temp),
        desc: data.weather[0].description[0].toUpperCase() + data.weather[0].description.slice(1),
        icon: `https://openweathermap.org/img/wn/${data.weather[0].icon}@4x.png`,
        details
      });
      playWeatherSound(data.weather[0].description);
    } catch {
      setWeather({ city: name, temp: '--', desc: 'Ошибка: город не найден', icon: '', details: {} });
    } finally {
      setLoading(false);
    }
  };

  const handleGeoWeather = () => {
    if (navigator.geolocation) {
      setLoading(true);
      navigator.geolocation.getCurrentPosition(async (pos) => {
        try {
          const lat = pos.coords.latitude;
          const lon = pos.coords.longitude;
          const apiKey = "61e36d8b6d0a171b685c5658b023c23a";
          const url = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${apiKey}&lang=ru&units=metric`;
          const res = await fetch(url);
          const data = await res.json();
          const details = {
            feels: Math.round(data.main.feels_like),
            pressure: Math.round(data.main.pressure * 0.750062),
            humidity: data.main.humidity,
            wind: `${Math.round(data.wind.speed)} м/с`,
            sunrise: formatTime(data.sys.sunrise),
            sunset: formatTime(data.sys.sunset)
          };
          setWeather({
            city: data.name,
            temp: Math.round(data.main.temp),
            desc: data.weather[0].description[0].toUpperCase() + data.weather[0].description.slice(1),
            icon: `https://openweathermap.org/img/wn/${data.weather[0].icon}@4x.png`,
            details
          });
          playWeatherSound(data.weather[0].description);
        } catch {
          setWeather({ city: '', temp: '--', desc: 'Ошибка определения', icon: '', details: {} });
        } finally {
          setLoading(false);
        }
      });
    }
  };

  const desc = weather?.desc || "";

  return (
    <ThemeProvider theme={theme}>
      <motion.div
        key={desc + (isNight ? 'night' : 'day')}
        style={{
          minHeight: '100vh',
          paddingTop: "env(safe-area-inset-top, 36px)",
          paddingBottom: 80,
          position: "relative",
          overflow: "hidden",
          fontFamily: 'Montserrat, Arial, sans-serif'
        }}
        animate={{ background: getBackground(desc, isNight) }}
        transition={{ duration: 1.2 }}
      >
        {isNight ? <NightGlow /> : null}
        {!isNight && (
          <>
            <motion.div
              style={{
                position: "absolute",
                zIndex: 0,
                top: -180,
                left: -100,
                width: 360,
                height: 360,
                borderRadius: "50%",
                background: "radial-gradient(circle at 60% 40%, #a7b8ff88 0%, #fff0 80%)",
                filter: "blur(54px)"
              }}
              animate={{
                scale: [1, 1.12, 0.98, 1],
                x: [0, 40, -30, 0],
                y: [0, 16, -22, 0],
                opacity: [0.42, 0.54, 0.36, 0.42]
              }}
              transition={{
                duration: 14,
                repeat: Infinity,
                repeatType: "mirror",
                ease: "easeInOut"
              }}
            />
            <motion.div
              style={{
                position: "absolute",
                zIndex: 0,
                bottom: -110,
                right: -90,
                width: 260,
                height: 260,
                borderRadius: "50%",
                background: "radial-gradient(circle at 60% 40%, #a1f8fff9 0%, #fff0 80%)",
                filter: "blur(44px)"
              }}
              animate={{
                scale: [1, 1.1, 0.97, 1],
                x: [0, -30, 24, 0],
                y: [0, -14, 22, 0],
                opacity: [0.31, 0.42, 0.36, 0.31]
              }}
              transition={{
                duration: 17,
                repeat: Infinity,
                repeatType: "mirror",
                ease: "easeInOut"
              }}
            />
          </>
        )}
        {desc && desc.toLowerCase().includes('дожд') && <RainEffect />}
        {desc && desc.toLowerCase().includes('снег') && <SnowEffect />}
        {desc && desc.toLowerCase().includes('ясно') && !isNight && <SunEffect />}

        <img
          src="/logo.png"
          alt="EasyWeather"
          style={{
            display: "block",
            margin: "32px auto 16px",
            width: 192,         // можешь увеличить/уменьшить по вкусу
            maxWidth: "90vw",
            height: "auto"
          }}
        />

        <motion.div
          initial="hidden"
          animate="visible"
          variants={{
            hidden: { opacity: 0, y: 20 },
            visible: {
              opacity: 1,
              y: 0,
              transition: { staggerChildren: 0.22 }
            }
          }}
          style={{
            maxWidth: 380,
            margin: "20px auto 0",
            textAlign: 'center',
            fontFamily: 'Montserrat, Arial, sans-serif'
          }}
        >
          {/* Лейбл над полем и само поле */}
          <motion.div variants={{ hidden: { opacity: 0, y: 20 }, visible: { opacity: 1, y: 0 } }}>
            <div
              style={{
                textAlign: "center",
                marginBottom: 6,
                color: "#fff",
                fontWeight: 500,
                fontSize: 18,
                letterSpacing: 0.3,
                fontFamily: "Montserrat, Arial, sans-serif"
              }}
            >
              Город
            </div>
            <TextField
              variant="outlined"
              fullWidth
              value={city}
              onChange={e => setCity(e.target.value)}
              sx={{
                background: "#fff",
                borderRadius: 3,
                height: 52,
                fontFamily: "Montserrat, Arial, sans-serif",
                '& .MuiInputBase-root': { height: 52 },
                '& .MuiInputBase-input': {
                  textAlign: "center",
                  fontWeight: 500,
                  fontSize: 20,
                  color: "#232942",
                  height: "52px !important",
                  padding: "0 14px",
                  fontFamily: "Montserrat, Arial, sans-serif"
                },
                '& .MuiOutlinedInput-notchedOutline': {
                  borderColor: "#cfd8dc"
                }
              }}
              InputProps={{
                style: { textAlign: "center", fontFamily: "Montserrat, Arial, sans-serif" }
              }}
              disabled={loading}
            />
          </motion.div>
          {/* Кнопка "Показать погоду" */}
          <motion.div variants={{ hidden: { opacity: 0, y: 20 }, visible: { opacity: 1, y: 0 } }}>
            <Button
              variant="contained"
              color="primary"
              fullWidth
              onClick={() => handleShowWeather()}
              disabled={loading}
              sx={{
                marginTop: 2,
                marginBottom: 1,
                borderRadius: 3,
                height: 52,
                fontSize: 18,
                fontWeight: 600,
                textTransform: "none",
                fontFamily: "Montserrat, Arial, sans-serif"
              }}
            >
              {loading ? <CircularProgress color="inherit" size={24} /> : 'Показать погоду'}
            </Button>
          </motion.div>
          {/* Кнопка "Погода по геолокации" */}
          <motion.div variants={{ hidden: { opacity: 0, y: 20 }, visible: { opacity: 1, y: 0 } }}>
            <Button
              variant="text"
              color="secondary"
              fullWidth
              sx={{
                mt: 1,
                color: "#fff",
                borderRadius: 3,
                fontSize: 18,
                fontWeight: 600,
                textTransform: "none",
                height: 52,
                fontFamily: "Montserrat, Arial, sans-serif"
              }}
              onClick={handleGeoWeather}
              disabled={loading}
            >
              Погода по геолокации
            </Button>
          </motion.div>
        </motion.div>

        {weather &&
          <WeatherCard {...weather} isNight={isNight} />
        }

        <AdBanner />
      </motion.div>
    </ThemeProvider>
  );
}

export default App;
