import { Card, CardContent, Typography } from '@mui/material';
import { motion } from "framer-motion";
import AnimatedDetails from './AnimatedDetails';

// Динамическая подложка под иконку — НЕ исчезает, всегда объёмная!
function getCircleStyles(desc) {
  if (!desc) return {
    background: "radial-gradient(circle at 60% 40%, #dee9fb 60%, #b0bcdf 100%)",
    boxShadow: "0 4px 24px #a5b4fc33"
  };
  desc = desc.toLowerCase();
  if (desc.includes('дожд')) return {
    background: "radial-gradient(circle at 60% 40%, #b9c6e9 60%, #3d5c9b 100%)",
    boxShadow: "0 4px 32px #7893c2bb"
  };
  if (desc.includes('гроза')) return {
    background: "radial-gradient(circle at 50% 50%, #8a9cb9 60%, #32394e 100%)",
    boxShadow: "0 4px 32px #32394e77"
  };
  if (desc.includes('ясно')) return {
    background: "radial-gradient(circle at 60% 35%, #fff4b1 70%, #ffd377 100%)",
    boxShadow: "0 4px 32px #ffa75177"
  };
  if (desc.includes('облачно')) return {
    background: "radial-gradient(circle at 60% 35%, #e1e9f7 60%, #bec2ce 100%)",
    boxShadow: "0 4px 32px #bec2ce88"
  };
  if (desc.includes('снег')) return {
    background: "radial-gradient(circle at 55% 40%, #eaf6fb 65%, #c8ddec 100%)",
    boxShadow: "0 4px 32px #bcdff177"
  };
  return {
    background: "radial-gradient(circle at 60% 40%, #dee9fb 60%, #b0bcdf 100%)",
    boxShadow: "0 4px 24px #a5b4fc33"
  };
}

function WeatherCard({ city, temp, desc, icon, details }) {
  const circleStyles = getCircleStyles(desc);

  return (
    <motion.div
      initial={{ opacity: 0, y: 80, scale: 0.93 }}
      animate={{ opacity: 1, y: 0, scale: 1 }}
      transition={{ duration: 0.7, type: "spring", bounce: 0.27 }}
      layout
      style={{ position: "relative" }}
    >
      {/* ВОЛНЫ и СВЕЧЕНИЯ — абсолютные элементы под карточкой! */}
      <motion.div
        style={{
          position: "absolute",
          zIndex: 0,
          top: -42,
          left: "10%",
          width: "80%",
          height: 160,
          borderRadius: "60% 40% 40% 60%/50% 60% 40% 50%",
          background: "linear-gradient(135deg, #a7b8ff55 0%, #89f0f6aa 100%)",
          filter: "blur(30px)",
          opacity: 0.7
        }}
        animate={{
          scale: [1, 1.07, 0.98, 1],
          y: [0, 8, -6, 0],
          x: [0, 8, -10, 0]
        }}
        transition={{
          duration: 9,
          repeat: Infinity,
          repeatType: "mirror",
          ease: "easeInOut"
        }}
      />
      <motion.div
        style={{
          position: "absolute",
          zIndex: 0,
          bottom: -44,
          left: "20%",
          width: "60%",
          height: 90,
          borderRadius: "60% 40% 40% 60%/50% 60% 40% 50%",
          background: "linear-gradient(90deg, #c7d2fe77 0%, #fae1ffa8 100%)",
          filter: "blur(32px)",
          opacity: 0.6
        }}
        animate={{
          scale: [1, 1.13, 1],
          y: [0, 7, 0],
          x: [0, -12, 0]
        }}
        transition={{
          duration: 11,
          repeat: Infinity,
          repeatType: "mirror",
          ease: "easeInOut"
        }}
      />

      <Card sx={{
        maxWidth: 380,
        margin: "32px auto",
        borderRadius: 6,
        boxShadow: 10,
        background: "rgba(255,255,255,0.88)",
        backdropFilter: "blur(3px)",
        position: "relative",
        zIndex: 2,
        overflow: "visible",
        transition: "box-shadow 0.5s, background 1s"
      }}>
        <CardContent sx={{ textAlign: 'center', position: "relative", zIndex: 3 }}>
          <Typography variant="h5" component="div" gutterBottom sx={{
            letterSpacing: 1,
            fontWeight: 500,
            color: "#32394e"
          }}>
            {city}
          </Typography>
          {/* ДИНАМИЧЕСКАЯ ОБЪЁМНАЯ ПОДЛОЖКА ПОД ИКОНКОЙ */}
          <motion.div
            style={{
              width: 92,
              height: 92,
              margin: "0 auto",
              borderRadius: "50%",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              marginBottom: 10,
              ...circleStyles,
              boxShadow: circleStyles.boxShadow,
              transition: "background 1.1s, box-shadow 0.7s"
            }}
            animate={circleStyles}
            transition={{
              background: { duration: 1.1, ease: "easeInOut" },
              boxShadow: { duration: 0.7, ease: "easeInOut" }
            }}
          >
            <motion.img
              src={icon}
              alt={desc}
              style={{ width: 56, height: 56, filter: "drop-shadow(0 2px 10px #bbb5)" }}
              initial={{ scale: 0.75, opacity: 0, rotate: -8 }}
              animate={{ scale: 1, opacity: 1, rotate: 0 }}
              transition={{ duration: 0.9, type: "spring" }}
            />
          </motion.div>

          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.13, duration: 0.6, type: "tween" }}
          >
            <Typography variant="h2" color="primary" gutterBottom sx={{
              fontWeight: 600,
              textShadow: "0 2px 18px #7e92ff22"
            }}>
              {temp}°C
            </Typography>
            <Typography color="text.secondary" sx={{
              fontSize: 22,
              mb: 2,
              letterSpacing: 0.6
            }}>
              {desc}
            </Typography>
          </motion.div>
          <AnimatedDetails details={details} />
        </CardContent>
      </Card>
    </motion.div>
  );
}

export default WeatherCard;
