import { Card, CardContent, Typography } from '@mui/material';
import { motion } from "framer-motion";
import AnimatedDetails from './AnimatedDetails'; // Импортируем новый компонент

function WeatherCard({ city, temp, desc, icon, details }) {
  return (
    <motion.div
      initial={{ opacity: 0, y: 80, scale: 0.95 }}
      animate={{ opacity: 1, y: 0, scale: 1 }}
      transition={{ duration: 0.8, type: "spring", bounce: 0.3 }}
    >
      <Card sx={{ maxWidth: 380, margin: "32px auto", borderRadius: 6, boxShadow: 8, background: "#f3f7fa" }}>
        <CardContent sx={{ textAlign: 'center' }}>
          <Typography variant="h5" component="div" gutterBottom>{city}</Typography>
          <motion.img
            src={icon}
            alt={desc}
            style={{ width: 92, height: 92 }}
            initial={{ scale: 0.7, opacity: 0, rotate: -12 }}
            animate={{ scale: 1, opacity: 1, rotate: 0 }}
            transition={{ duration: 0.8, type: "spring" }}
          />
          <Typography variant="h2" color="primary" gutterBottom>{temp}°C</Typography>
          <Typography color="text.secondary" sx={{ fontSize: 22, mb: 2 }}>{desc}</Typography>
          {/* Вот тут вызываем анимированный блок деталей */}
          <AnimatedDetails details={details} />
        </CardContent>
      </Card>
    </motion.div>
  );
}

export default WeatherCard;
