import { Card, CardContent, Typography, TextField, Button } from '@mui/material';
import { useState } from 'react';
import { fetchWeather } from './weatherApi';

function WeatherCard({ city, temp, desc }) {
  return (
    <Card sx={{ maxWidth: 340, margin: "32px auto", borderRadius: 4, boxShadow: 6 }}>
      <CardContent sx={{ textAlign: 'center' }}>
        <Typography variant="h5" component="div" gutterBottom>
          {city}
        </Typography>
        <Typography variant="h2" component="div" color="primary" gutterBottom>
          {temp}°C
        </Typography>
        <Typography color="text.secondary">{desc}</Typography>
      </CardContent>
    </Card>
  );
}

function App() {
  const [city, setCity] = useState('Москва');
  const [temp, setTemp] = useState(18);
  const [desc, setDesc] = useState('Пасмурно');
  const [loading, setLoading] = useState(false);

  const handleShowWeather = async () => {
    setLoading(true);
    try {
      const data = await fetchWeather(city);
      setTemp(Math.round(data.main.temp));
      setDesc(data.weather[0].description);
    } catch (e) {
      setTemp('--');
      setDesc('Ошибка: город не найден');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ minHeight: '100vh', background: '#f5f7fa', paddingTop: 40 }}>
      <WeatherCard city={city} temp={temp} desc={desc} />

      <div style={{ maxWidth: 340, margin: "24px auto", textAlign: 'center' }}>
        <TextField
          label="Город"
          variant="outlined"
          fullWidth
          value={city}
          onChange={e => setCity(e.target.value)}
          sx={{ marginBottom: 2 }}
          disabled={loading}
        />
        <Button
          variant="contained"
          color="primary"
          fullWidth
          onClick={handleShowWeather}
          disabled={loading}
        >
          {loading ? 'Загрузка...' : 'Показать погоду'}
        </Button>
      </div>
    </div>
  );
}

export default App;
