import { Card, CardContent, Typography } from '@mui/material';
import { motion } from "framer-motion";
import AnimatedDetails from './AnimatedDetails';

// Возвращает и цветовую схему подложки, и box-shadow под иконку — по погоде!
function getCircleStyles(desc) {
  if (!desc) return {
    background: "linear-gradient(135deg, #e0e7ff80 0%, #f5f7fa80 100%)",
    boxShadow: "0 4px 24px #a5b4fc33"
  };
  desc = desc.toLowerCase();
  if (desc.includes('дожд')) return {
    background: "linear-gradient(135deg, #b9c6e9cc 0%, #7893c2bb 100%)",
    boxShadow: "0 4px 32px #7893c299"
  };
  if (desc.includes('гроза')) return {
    background: "linear-gradient(135deg, #838cb8cc 0%, #32394ecc 100%)",
    boxShadow: "0 4px 32px #32394e77"
  };
  if (desc.includes('ясно')) return {
    background: "linear-gradient(135deg, #ffe259cc 0%, #ffa751bb 100%)",
    boxShadow: "0 4px 32px #ffa75177"
  };
  if (desc.includes('облачно')) return {
    background: "linear-gradient(135deg, #dbeafecc 0%, #6b7280cc 100%)",
    boxShadow: "0 4px 32px #6b728099"
  };
  if (desc.includes('снег')) return {
    background: "linear-gradient(135deg, #f3f4f6cc 0%, #bcdff1cc 100%)",
    boxShadow: "0 4px 32px #bcdff177"
  };
  return {
    background: "linear-gradient(135deg, #e0e7ff80 0%, #f5f7fa80 100%)",
    boxShadow: "0 4px 24px #a5b4fc33"
  };
}

function WeatherCard({ city, temp, desc, icon, details }) {
  const circleStyles = getCircleStyles(desc);

  return (
    <motion.div
      initial={{ opacity: 0, y: 80, scale: 0.93 }}
      animate={{ opacity: 1, y: 0, scale: 1 }}
      transition={{ duration: 0.7, type: "spring", bounce: 0.27 }}
      layout
    >
      <Card sx={{
        maxWidth: 380,
        margin: "32px auto",
        borderRadius: 6,
        boxShadow: 10,
        background: "rgba(255,255,255,0.86)",
        backdropFilter: "blur(2.5px)",
        transition: "box-shadow 0.4s, background 0.8s"
      }}>
        <CardContent sx={{ textAlign: 'center' }}>
          <Typography variant="h5" component="div" gutterBottom sx={{
            letterSpacing: 1,
            fontWeight: 500,
            color: "#32394e"
          }}>
            {city}
          </Typography>
          {/* ДИНАМИЧЕСКАЯ ПОДЛОЖКА с плавным переходом */}
          <motion.div
            style={{
              width: 92,
              height: 92,
              margin: "0 auto",
              borderRadius: "50%",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              marginBottom: 10,
              ...circleStyles,
              transition: "background 1s, box-shadow 0.6s"
            }}
            animate={circleStyles}
          >
            <motion.img
              src={icon}
              alt={desc}
              style={{ width: 56, height: 56, filter: "drop-shadow(0 2px 10px #bbb5)" }}
              initial={{ scale: 0.75, opacity: 0, rotate: -8 }}
              animate={{ scale: 1, opacity: 1, rotate: 0 }}
              transition={{ duration: 0.9, type: "spring" }}
            />
          </motion.div>

          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.13, duration: 0.6, type: "tween" }}
          >
            <Typography variant="h2" color="primary" gutterBottom sx={{
              fontWeight: 600,
              textShadow: "0 2px 18px #7e92ff22"
            }}>
              {temp}°C
            </Typography>
            <Typography color="text.secondary" sx={{
              fontSize: 22,
              mb: 2,
              letterSpacing: 0.6
            }}>
              {desc}
            </Typography>
          </motion.div>
          <AnimatedDetails details={details} />
        </CardContent>
      </Card>
    </motion.div>
  );
}

export default WeatherCard;
