import { Card, Typography } from '@mui/material';
import { motion } from "framer-motion";

function WeatherCard({ city, temp, desc, icon, details, isNight, forecast }) {
  // Цвета:
  const skyBlue = "#c7e0f5";
  const tempBlue = "#2498dc";
  const cardBg = "#e9f1f8";

  return (
    <Card
      style={{
        margin: "0 auto",
        maxWidth: 360,
        borderRadius: 24,
        boxShadow: "0 8px 40px #0002",
        background: isNight
          ? "linear-gradient(135deg, #2d3148 0%, #232942 100%)"
          : cardBg,
        color: isNight ? "#dde7fa" : "#232942",
        position: "relative",
        padding: "24px 18px 18px",
        top: 0
      }}
    >
      <Typography variant="h5" align="center" sx={{ fontWeight: 600 }}>
        {city}
      </Typography>
      
      {/* КРУГЛЫЙ ФОН ПОД ИКОНКОЙ */}
      <div style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        margin: "14px auto 2px",
        width: 80,
        height: 80,
        borderRadius: "50%",
        background: skyBlue,
        boxShadow: "0 2px 12px #c7e0f533"
      }}>
        <img
          src={icon}
          alt={desc}
          style={{
            width: 52,
            height: 52,
            display: "block"
          }}
        />
      </div>

      {/* Температура */}
      <Typography
        variant="h2"
        align="center"
        sx={{ 
          fontWeight: 700,
          margin: 0,
          color: isNight ? "#6ec6ff" : tempBlue,
          letterSpacing: 2
        }}
      >
        {temp}°
      </Typography>

      {/* Описание погоды */}
      <Typography
        variant="subtitle1"
        align="center"
        sx={{
          marginTop: "4px",
          marginBottom: forecast && forecast.length > 0 ? "6px" : "12px",
          fontWeight: 500
        }}
      >
        {desc}
      </Typography>

      {/* Карусель прогноза */}
      {forecast && forecast.length > 0 && (
        <motion.div
          style={{
            display: "flex",
            gap: 16,
            overflowX: "auto",
            margin: "0 auto 12px",
            justifyContent: "center",
            padding: "6px 0",
            WebkitOverflowScrolling: "touch"
          }}
          initial={{ opacity: 0, x: 40 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.55, delay: 0.2 }}
        >
          {forecast.map((item, idx) => (
            <div
              key={idx}
              style={{
                background: "#f6faff",
                borderRadius: 16,
                minWidth: 64,
                padding: "7px 7px",
                boxShadow: "0 2px 10px #c7e0f511",
                textAlign: "center"
              }}
            >
              <div style={{ fontWeight: 500, fontSize: 15 }}>{item.time}</div>
              <img
                src={item.icon}
                alt=""
                style={{ width: 28, height: 28, margin: "1px 0" }}
              />
              <div style={{ fontWeight: 600, fontSize: 16 }}>{item.temp}°</div>
            </div>
          ))}
        </motion.div>
      )}

      {/* Детали */}
      {details && (
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          marginTop: 12,
          gap: 8
        }}>
          <div>
            <div style={{ fontWeight: 500, fontSize: 14 }}>Ощущается</div>
            <div style={{ fontWeight: 600, fontSize: 16 }}>{details.feels}°</div>
          </div>
          <div>
            <div style={{ fontWeight: 500, fontSize: 14 }}>Давление</div>
            <div style={{ fontWeight: 600, fontSize: 16 }}>{details.pressure} мм</div>
          </div>
          <div>
            <div style={{ fontWeight: 500, fontSize: 14 }}>Влажность</div>
            <div style={{ fontWeight: 600, fontSize: 16 }}>{details.humidity}%</div>
          </div>
          <div>
            <div style={{ fontWeight: 500, fontSize: 14 }}>Ветер</div>
            <div style={{ fontWeight: 600, fontSize: 16 }}>{details.wind}</div>
          </div>
        </div>
      )}
    </Card>
  );
}

export default WeatherCard;
