import { Typography, TextField, Button, CircularProgress } from '@mui/material';
import { useState } from 'react';
import { fetchWeather } from './weatherApi';
import WeatherCard from './WeatherCard';
import AdBanner from './AdBanner';
import { motion } from "framer-motion";

// Новый более читаемый светлый фон
function getBackground(desc, isNight) {
  if (isNight) return "linear-gradient(135deg, #232942 0%, #465077 100%)";
  if (!desc) return "linear-gradient(135deg, #f3f5fa 0%, #e2e8f0 100%)";
  desc = desc.toLowerCase();
  if (desc.includes('дожд')) return "linear-gradient(135deg, #e3e9f7 0%, #a7b6dd 100%)";
  if (desc.includes('гроза')) return "linear-gradient(135deg, #32394e 0%, #5a6a90 100%)";
  if (desc.includes('ясно')) return "linear-gradient(135deg, #f5e8bc 0%, #f9d67a 100%)";
  if (desc.includes('облачно')) return "linear-gradient(135deg, #e5ecf7 0%, #a0aec0 100%)";
  if (desc.includes('снег')) return "linear-gradient(135deg, #f3f4f6 0%, #bcdff1 100%)";
  return "linear-gradient(135deg, #f3f5fa 0%, #e2e8f0 100%)";
}

function formatTime(ts) {
  const date = new Date(ts * 1000);
  return date.toLocaleTimeString('ru-RU', { hour: '2-digit', minute: '2-digit' });
}

function playWeatherSound(desc) {
  let audio;
  if (desc.includes('дожд')) audio = new Audio('/rain.wav');
  else if (desc.includes('гроз')) audio = new Audio('/thunder.wav');
  else if (desc.includes('ясно')) audio = new Audio('/sun.wav');
  else return;
  audio.volume = 0.2;
  audio.play();
}

// Эффекты
function RainEffect() {
  return (
    <>
      {[...Array(15)].map((_, i) => (
        <motion.div
          key={i}
          style={{
            position: "absolute",
            left: `${7 + Math.random() * 86}%`,
            top: `${-18 + Math.random() * 18}%`,
            width: 7 + Math.random() * 8,
            height: 24 + Math.random() * 16,
            background: "rgba(92,160,240,0.15)",
            borderRadius: "50%",
            filter: "blur(1.2px)",
            zIndex: 1
          }}
          animate={{
            y: ["0%", "130%"],
            opacity: [0.35, 0.6, 0]
          }}
          transition={{
            duration: 3.7 + Math.random() * 2.3,
            repeat: Infinity,
            delay: i * 0.4
          }}
        />
      ))}
    </>
  );
}
function SnowEffect() {
  return (
    <>
      {[...Array(16)].map((_, i) => (
        <motion.div
          key={i}
          style={{
            position: "absolute",
            left: `${3 + Math.random() * 92}%`,
            top: `${-12 + Math.random() * 10}%`,
            width: 10 + Math.random() * 8,
            height: 10 + Math.random() * 8,
            background: "rgba(230,240,255,0.21)",
            borderRadius: "50%",
            filter: "blur(1.7px)",
            zIndex: 1
          }}
          animate={{
            y: ["0%", "140%"],
            x: [0, 8 - Math.random() * 16, 0],
            opacity: [0.34, 0.56, 0.25]
          }}
          transition={{
            duration: 7.7 + Math.random() * 2.6,
            repeat: Infinity,
            delay: i * 0.7
          }}
        />
      ))}
    </>
  );
}
function SunEffect() {
  return (
    <motion.div
      style={{
        position: "absolute",
        right: 80,
        top: 44,
        width: 112,
        height: 112,
        borderRadius: "50%",
        background: "radial-gradient(circle at 60% 40%, #fffcd9 50%, #fff66c88 100%)",
        boxShadow: "0 0 60px 24px #fffec955",
        zIndex: 1,
        opacity: 0.89
      }}
      animate={{
        scale: [1, 1.06, 0.98, 1],
        filter: [
          "blur(0.5px)",
          "blur(3.3px)",
          "blur(1.5px)",
          "blur(0.5px)"
        ]
      }}
      transition={{
        duration: 8,
        repeat: Infinity,
        repeatType: "mirror"
      }}
    />
  );
}
function NightGlow() {
  return (
    <>
      <motion.div
        style={{
          position: "absolute",
          left: "10%",
          top: "-12%",
          width: "78%",
          height: 220,
          borderRadius: "50%",
          background: "linear-gradient(135deg, #1e224477 0%, #14182aee 100%)",
          filter: "blur(34px)",
          opacity: 0.7,
          zIndex: 0
        }}
        animate={{ scale: [1, 1.12, 0.93, 1] }}
        transition={{ duration: 13, repeat: Infinity, repeatType: "mirror" }}
      />
      <motion.div
        style={{
          position: "absolute",
          left: "70%",
          bottom: "-9%",
          width: 180,
          height: 120,
          borderRadius: "50%",
          background: "linear-gradient(135deg, #7d86c744 0%, #25284dff 100%)",
          filter: "blur(32px)",
          opacity: 0.4,
          zIndex: 0
        }}
        animate={{ scale: [1, 1.07, 0.98, 1] }}
        transition={{ duration: 17, repeat: Infinity, repeatType: "mirror" }}
      />
    </>
  );
}
function isNightNow() {
  const h = new Date().getHours();
  return h < 6 || h >= 21;
}

function App() {
  const [city, setCity] = useState('Москва');
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);

  const isNight = isNightNow();

  const handleShowWeather = async (name = city) => {
    setLoading(true);
    try {
      const data = await fetchWeather(name);
      const details = {
        feels: Math.round(data.main.feels_like),
        pressure: Math.round(data.main.pressure * 0.750062),
        humidity: data.main.humidity,
        wind: `${Math.round(data.wind.speed)} м/с`,
        sunrise: formatTime(data.sys.sunrise),
        sunset: formatTime(data.sys.sunset)
      };
      setWeather({
        city: data.name,
        temp: Math.round(data.main.temp),
        desc: data.weather[0].description[0].toUpperCase() + data.weather[0].description.slice(1),
        icon: `https://openweathermap.org/img/wn/${data.weather[0].icon}@4x.png`,
        details
      });
      playWeatherSound(data.weather[0].description);
    } catch {
      setWeather({ city: name, temp: '--', desc: 'Ошибка: город не найден', icon: '', details: {} });
    } finally {
      setLoading(false);
    }
  };

  const handleGeoWeather = () => {
    if (navigator.geolocation) {
      setLoading(true);
      navigator.geolocation.getCurrentPosition(async (pos) => {
        try {
          const lat = pos.coords.latitude;
          const lon = pos.coords.longitude;
          const apiKey = "61e36d8b6d0a171b685c5658b023c23a";
          const url = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${apiKey}&lang=ru&units=metric`;
          const res = await fetch(url);
          const data = await res.json();
          const details = {
            feels: Math.round(data.main.feels_like),
            pressure: Math.round(data.main.pressure * 0.750062),
            humidity: data.main.humidity,
            wind: `${Math.round(data.wind.speed)} м/с`,
            sunrise: formatTime(data.sys.sunrise),
            sunset: formatTime(data.sys.sunset)
          };
          setWeather({
            city: data.name,
            temp: Math.round(data.main.temp),
            desc: data.weather[0].description[0].toUpperCase() + data.weather[0].description.slice(1),
            icon: `https://openweathermap.org/img/wn/${data.weather[0].icon}@4x.png`,
            details
          });
          playWeatherSound(data.weather[0].description);
        } catch {
          setWeather({ city: '', temp: '--', desc: 'Ошибка определения', icon: '', details: {} });
        } finally {
          setLoading(false);
        }
      });
    }
  };

  const desc = weather?.desc || "";

  return (
    <motion.div
      key={desc + (isNight ? 'night' : 'day')}
      style={{
        minHeight: '100vh',
        paddingBottom: 80,
        position: "relative",
        overflow: "hidden"
      }}
      animate={{ background: getBackground(desc, isNight) }}
      transition={{ duration: 1.2 }}
    >
      {isNight ? <NightGlow /> : null}
      {!isNight && (
        <>
          <motion.div
            style={{
              position: "absolute",
              zIndex: 0,
              top: -180,
              left: -100,
              width: 360,
              height: 360,
              borderRadius: "50%",
              background: "radial-gradient(circle at 60% 40%, #a7b8ff88 0%, #fff0 80%)",
              filter: "blur(54px)"
            }}
            animate={{
              scale: [1, 1.12, 0.98, 1],
              x: [0, 40, -30, 0],
              y: [0, 16, -22, 0],
              opacity: [0.42, 0.54, 0.36, 0.42]
            }}
            transition={{
              duration: 14,
              repeat: Infinity,
              repeatType: "mirror",
              ease: "easeInOut"
            }}
          />
          <motion.div
            style={{
              position: "absolute",
              zIndex: 0,
              bottom: -110,
              right: -90,
              width: 260,
              height: 260,
              borderRadius: "50%",
              background: "radial-gradient(circle at 60% 40%, #a1f8fff9 0%, #fff0 80%)",
              filter: "blur(44px)"
            }}
            animate={{
              scale: [1, 1.1, 0.97, 1],
              x: [0, -30, 24, 0],
              y: [0, -14, 22, 0],
              opacity: [0.31, 0.42, 0.36, 0.31]
            }}
            transition={{
              duration: 17,
              repeat: Infinity,
              repeatType: "mirror",
              ease: "easeInOut"
            }}
          />
        </>
      )}
      {desc && desc.toLowerCase().includes('дожд') && <RainEffect />}
      {desc && desc.toLowerCase().includes('снег') && <SnowEffect />}
      {desc && desc.toLowerCase().includes('ясно') && !isNight && <SunEffect />}

      <Typography variant="h4" align="center" sx={{ pt: 3, fontWeight: 700, letterSpacing: 2, color: isNight ? "#dde7fa" : "#4f46e5" }}>
        EasyWeather
      </Typography>

      <motion.div
        initial="hidden"
        animate="visible"
        variants={{
          hidden: { opacity: 0, y: 20 },
          visible: {
            opacity: 1,
            y: 0,
            transition: { staggerChildren: 0.22 }
          }
        }}
        style={{ maxWidth: 380, margin: "20px auto 0", textAlign: 'center' }}
      >
        {/* Лейбл над полем и само поле */}
        <motion.div variants={{ hidden: { opacity: 0, y: 20 }, visible: { opacity: 1, y: 0 } }}>
          <div
            style={{
              textAlign: "center",
              marginBottom: 6,
              color: "#fff",
              fontWeight: 500,
              fontSize: 18,
              letterSpacing: 0.3
            }}
          >
            Город
          </div>
          <TextField
            variant="outlined"
            fullWidth
            value={city}
            onChange={e => setCity(e.target.value)}
            sx={{
              background: "#fff",
              borderRadius: 4,
              height: 52,
              '& .MuiInputBase-root': { height: 52 },
              '& .MuiInputBase-input': {
                textAlign: "center",
                fontWeight: 500,
                fontSize: 20,
                color: "#232942",
                height: "52px !important",
                padding: "0 14px"
              },
              '& .MuiOutlinedInput-notchedOutline': {
                borderColor: "#cfd8dc"
              }
            }}
            InputProps={{
              style: { textAlign: "center" }
            }}
            disabled={loading}
          />
        </motion.div>
        {/* Кнопка "Показать погоду" */}
        <motion.div variants={{ hidden: { opacity: 0, y: 20 }, visible: { opacity: 1, y: 0 } }}>
          <Button
            variant="contained"
            color="primary"
            fullWidth
            onClick={() => handleShowWeather()}
            disabled={loading}
            sx={{
              marginTop: 2,
              marginBottom: 1,
              borderRadius: 3,
              height: 52,
              fontSize: 18,
              fontWeight: 600,
              textTransform: "none"
            }}
          >
            {loading ? <CircularProgress color="inherit" size={24} /> : 'Показать погоду'}
          </Button>
        </motion.div>
        {/* Кнопка "Погода по геолокации" */}
        <motion.div variants={{ hidden: { opacity: 0, y: 20 }, visible: { opacity: 1, y: 0 } }}>
          <Button
            variant="text"
            color="secondary"
            fullWidth
            sx={{
              mt: 1,
              color: "#fff",
              borderRadius: 3,
              fontSize: 18,
              fontWeight: 600,
              textTransform: "none",
              height: 52
            }}
            onClick={handleGeoWeather}
            disabled={loading}
          >
            Погода по геолокации
          </Button>
        </motion.div>
      </motion.div>

      {weather &&
        <WeatherCard {...weather} isNight={isNight} />
      }

      <AdBanner />
    </motion.div>
  );
}

export default App;
