import { Typography, TextField, Button, CircularProgress } from '@mui/material';
import { useState } from 'react';
import { fetchWeather } from './weatherApi';
import WeatherCard from './WeatherCard';       // Используем отдельный компонент карточки
import AdBanner from './AdBanner';
import { motion } from "framer-motion";

// Функция для подбора красивого градиента по погоде
function getBackground(desc) {
  if (!desc) return "linear-gradient(135deg, #e0e7ff 0%, #f5f7fa 100%)";
  desc = desc.toLowerCase();
  if (desc.includes('дожд')) return "linear-gradient(135deg, #b9c6e9 0%, #7893c2 100%)";
  if (desc.includes('гроза')) return "linear-gradient(135deg, #838cb8 0%, #32394e 100%)";
  if (desc.includes('ясно')) return "linear-gradient(135deg, #ffe259 0%, #ffa751 100%)";
  if (desc.includes('облачно')) return "linear-gradient(135deg, #dbeafe 0%, #6b7280 100%)";
  if (desc.includes('снег')) return "linear-gradient(135deg, #f3f4f6 0%, #bcdff1 100%)";
  return "linear-gradient(135deg, #e0e7ff 0%, #f5f7fa 100%)";
}

function formatTime(ts) {
  const date = new Date(ts * 1000);
  return date.toLocaleTimeString('ru-RU', { hour: '2-digit', minute: '2-digit' });
}

function playWeatherSound(desc) {
  let audio;
  if (desc.includes('дожд')) audio = new Audio('/rain.wav');
  else if (desc.includes('гроз')) audio = new Audio('/thunder.wav');
  else if (desc.includes('ясно')) audio = new Audio('/sun.wav');
  else return;
  audio.volume = 0.2;
  audio.play();
}

function App() {
  const [city, setCity] = useState('Москва');
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleShowWeather = async (name = city) => {
    setLoading(true);
    try {
      const data = await fetchWeather(name);
      const details = {
        feels: Math.round(data.main.feels_like),
        pressure: Math.round(data.main.pressure * 0.750062), // гПа -> мм рт.ст.
        humidity: data.main.humidity,
        wind: `${Math.round(data.wind.speed)} м/с`,
        sunrise: formatTime(data.sys.sunrise),
        sunset: formatTime(data.sys.sunset)
      };
      setWeather({
        city: data.name,
        temp: Math.round(data.main.temp),
        desc: data.weather[0].description[0].toUpperCase() + data.weather[0].description.slice(1),
        icon: `https://openweathermap.org/img/wn/${data.weather[0].icon}@4x.png`,
        details
      });
      playWeatherSound(data.weather[0].description);
    } catch {
      setWeather({ city: name, temp: '--', desc: 'Ошибка: город не найден', icon: '', details: {} });
    } finally {
      setLoading(false);
    }
  };

  const handleGeoWeather = () => {
    if (navigator.geolocation) {
      setLoading(true);
      navigator.geolocation.getCurrentPosition(async (pos) => {
        try {
          const lat = pos.coords.latitude;
          const lon = pos.coords.longitude;
          const apiKey = "61e36d8b6d0a171b685c5658b023c23a"; // такой же, как в weatherApi.js
          const url = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${apiKey}&lang=ru&units=metric`;
          const res = await fetch(url);
          const data = await res.json();
          const details = {
            feels: Math.round(data.main.feels_like),
            pressure: Math.round(data.main.pressure * 0.750062),
            humidity: data.main.humidity,
            wind: `${Math.round(data.wind.speed)} м/с`,
            sunrise: formatTime(data.sys.sunrise),
            sunset: formatTime(data.sys.sunset)
          };
          setWeather({
            city: data.name,
            temp: Math.round(data.main.temp),
            desc: data.weather[0].description[0].toUpperCase() + data.weather[0].description.slice(1),
            icon: `https://openweathermap.org/img/wn/${data.weather[0].icon}@4x.png`,
            details
          });
          playWeatherSound(data.weather[0].description);
        } catch {
          setWeather({ city: '', temp: '--', desc: 'Ошибка определения', icon: '', details: {} });
        } finally {
          setLoading(false);
        }
      });
    }
  };

  return (
    <motion.div
      key={weather?.desc || 'default-bg'}
      style={{
        minHeight: '100vh',
        paddingBottom: 80,
        position: "relative"
      }}
      animate={{ background: getBackground(weather?.desc) }}
      transition={{ duration: 1.2 }}
    >
      <Typography variant="h4" align="center" sx={{ pt: 3, fontWeight: 700, letterSpacing: 2, color: "#4f46e5" }}>
        EasyWeather
      </Typography>

      <motion.div
        initial="hidden"
        animate="visible"
        variants={{
          hidden: { opacity: 0, y: 20 },
          visible: {
            opacity: 1,
            y: 0,
            transition: { staggerChildren: 0.22 }
          }
        }}
        style={{ maxWidth: 380, margin: "20px auto 0", textAlign: 'center' }}
      >
        {/* Поле ввода города */}
        <motion.div variants={{ hidden: { opacity: 0, y: 20 }, visible: { opacity: 1, y: 0 } }}>
          <TextField
            label="Город"
            variant="outlined"
            fullWidth
            value={city}
            onChange={e => setCity(e.target.value)}
            sx={{ marginBottom: 2 }}
            disabled={loading}
          />
        </motion.div>
        {/* Кнопка "Показать погоду" */}
        <motion.div variants={{ hidden: { opacity: 0, y: 20 }, visible: { opacity: 1, y: 0 } }}>
          <Button
            variant="contained"
            color="primary"
            fullWidth
            onClick={() => handleShowWeather()}
            disabled={loading}
            sx={{ marginBottom: 1 }}
          >
            {loading ? <CircularProgress color="inherit" size={24} /> : 'Показать погоду'}
          </Button>
        </motion.div>
        {/* Кнопка "Погода по геолокации" */}
        <motion.div variants={{ hidden: { opacity: 0, y: 20 }, visible: { opacity: 1, y: 0 } }}>
          <Button
            variant="text"
            color="secondary"
            fullWidth
            sx={{ mt: 1 }}
            onClick={handleGeoWeather}
            disabled={loading}
          >
            Погода по геолокации
          </Button>
        </motion.div>
      </motion.div>

      {weather &&
        <WeatherCard {...weather} />
      }

      <AdBanner />
    </motion.div>
  );
}

export default App;
