import { Typography, TextField, Button, CircularProgress } from '@mui/material';
import { useState } from 'react';
import { fetchWeather, fetchForecast } from './weatherApi';
import WeatherCard from './WeatherCard';
import AdBanner from './AdBanner';
import { motion } from "framer-motion";
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  typography: {
    fontFamily: [
      'Montserrat',
      'Arial',
      'sans-serif',
    ].join(','),
  },
});

function getBackground(desc, isNight) {
  if (isNight) return "linear-gradient(135deg, #232942 0%, #465077 100%)";
  if (!desc) return "linear-gradient(135deg, #c7e0f5 0%, #b4c7e7 40%, #b3c2d7 100%)";
  desc = desc.toLowerCase();
  if (desc.includes('дожд')) return "linear-gradient(135deg, #b3c2d7 0%, #90a4c3 100%)";
  if (desc.includes('гроза')) return "linear-gradient(135deg, #aeb7c6 0%, #8394b0 100%)";
  if (desc.includes('ясно')) return "linear-gradient(135deg, #ffe789 0%, #bde1ff 100%)";
  if (desc.includes('облачно')) return "linear-gradient(135deg, #c7e0f5 0%, #a0aec0 100%)";
  if (desc.includes('снег')) return "linear-gradient(135deg, #e0e8f0 0%, #b3c2d7 100%)";
  return "linear-gradient(135deg, #c7e0f5 0%, #b4c7e7 40%, #b3c2d7 100%)";
}

function formatTime(ts) {
  const date = new Date(ts * 1000);
  return date.toLocaleTimeString('ru-RU', { hour: '2-digit', minute: '2-digit' });
}

function playWeatherSound(desc) {
  let audio;
  if (desc.includes('дожд')) audio = new Audio('/rain.wav');
  else if (desc.includes('гроз')) audio = new Audio('/thunder.wav');
  else if (desc.includes('ясно')) audio = new Audio('/sun.wav');
  else return;
  audio.volume = 0.2;
  audio.play();
}

// Весь твой эффект для дождя
function RainEffect() {
  return (
    <>
      {[...Array(15)].map((_, i) => (
        <motion.div
          key={i}
          style={{
            position: "absolute",
            left: `${7 + Math.random() * 86}%`,
            top: `${-18 + Math.random() * 18}%`,
            width: 7 + Math.random() * 8,
            height: 24 + Math.random() * 16,
            background: "rgba(92,160,240,0.15)",
            borderRadius: "50%",
            filter: "blur(1.2px)",
            zIndex: 1
          }}
          animate={{
            y: ["0%", "130%"],
            opacity: [0.35, 0.6, 0]
          }}
          transition={{
            duration: 3.7 + Math.random() * 2.3,
            repeat: Infinity,
            delay: i * 0.4
          }}
        />
      ))}
    </>
  );
}

// Снег
function SnowEffect() {
  return (
    <>
      {[...Array(16)].map((_, i) => (
        <motion.div
          key={i}
          style={{
            position: "absolute",
            left: `${3 + Math.random() * 92}%`,
            top: `${-12 + Math.random() * 10}%`,
            width: 10 + Math.random() * 8,
            height: 10 + Math.random() * 8,
            background: "rgba(230,240,255,0.21)",
            borderRadius: "50%",
            filter: "blur(1.7px)",
            zIndex: 1
          }}
          animate={{
            y: ["0%", "140%"],
            x: [0, 8 - Math.random() * 16, 0],
            opacity: [0.34, 0.56, 0.25]
          }}
          transition={{
            duration: 7.7 + Math.random() * 2.6,
            repeat: Infinity,
            delay: i * 0.7
          }}
        />
      ))}
    </>
  );
}

function SunEffect() {
  return (
    <motion.div
      style={{
        position: "absolute",
        right: 80,
        top: 44,
        width: 112,
        height: 112,
        borderRadius: "50%",
        background: "radial-gradient(circle at 60% 40%, #fffcd9 50%, #fff66c88 100%)",
        boxShadow: "0 0 60px 24px #fffec955",
        zIndex: 1,
        opacity: 0.89
      }}
      animate={{
        scale: [1, 1.06, 0.98, 1],
        filter: [
          "blur(0.5px)",
          "blur(3.3px)",
          "blur(1.5px)",
          "blur(0.5px)"
        ]
      }}
      transition={{
        duration: 8,
        repeat: Infinity,
        repeatType: "mirror"
      }}
    />
  );
}

function NightGlow() {
  return (
    <>
      <motion.div
        style={{
          position: "absolute",
          left: "10%",
          top: "-12%",
          width: "78%",
          height: 220,
          borderRadius: "50%",
          background: "linear-gradient(135deg, #1e224477 0%, #14182aee 100%)",
          filter: "blur(34px)",
          opacity: 0.7,
          zIndex: 0
        }}
        animate={{ scale: [1, 1.12, 0.93, 1] }}
        transition={{ duration: 13, repeat: Infinity, repeatType: "mirror" }}
      />
      <motion.div
        style={{
          position: "absolute",
          left: "70%",
          bottom: "-9%",
          width: 180,
          height: 120,
          borderRadius: "50%",
